    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_product_list);

        // Hide both status bar and navigation bar
        Window window = getWindow();
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.R) {
            Objects.requireNonNull(window.getInsetsController()).hide(
                    WindowInsets.Type.statusBars() | WindowInsets.Type.navigationBars()
            );
            // Optional: Set behavior for immersive mode
            window.getInsetsController().setSystemBarsBehavior(
                    WindowInsetsController.BEHAVIOR_SHOW_TRANSIENT_BARS_BY_SWIPE
            );
        }
        // Initialize views
        initViews();

        // Initialize DataManager
        dataManager = new DataManager(this);

        SharedPreferences sharedPreferences = getSharedPreferences("UserPrefs", MODE_PRIVATE);
        userId = Integer.parseInt(sharedPreferences.getString("userId", "-1"));

        // Get userId from Intent
        if (userId == -1) {
            Log.e(TAG, "Invalid userId received");
            Toast.makeText(this, "Invalid session. Please log in again.", Toast.LENGTH_SHORT).show();
            startActivity(new Intent(this, MainActivity.class));
            finish();
            return;
        }

        Log.d(TAG, "User ID: " + userId);

        // Load and display products
        loadProducts();

        // Setup listeners
        setupListeners();

        // Setup banner
        setupBanner();
        startAutoSlide();
    }

